import React, { useState } from "react";
import axios from "axios";

function App() {
  const [movieName, setMovieName] = useState("");
  const [recommendations, setRecommendations] = useState([]);
  const [error, setError] = useState("");

  const fetchRecommendations = async () => {
    setError("");
    setRecommendations([]);

    try {
      const response = await axios.post("http://127.0.0.1:5000/recommend_movies", { movie_name: movieName });
      setRecommendations(response.data);
    } catch (err) {
      setError("Movie not found. Try another title.");
    }
  };

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h1>Movie Recommendation System</h1>
      <input 
        type="text" 
        placeholder="Enter movie name" 
        value={movieName} 
        onChange={(e) => setMovieName(e.target.value)} 
        style={{ padding: "10px", width: "300px", fontSize: "16px" }} 
      />
      <button onClick={fetchRecommendations} style={{ padding: "10px", marginLeft: "10px" }}>Get Recommendations</button>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {recommendations.length > 0 && (
        <div>
          <h2>Recommended Movies</h2>
          <ul>
            {recommendations.map((movie, index) => (
              <li key={index}><b>{movie.title}</b> - {movie.overview}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
